import React, { useState } from 'react';
import axios from 'axios';
import './ReadingForm.css';

const zodiacSigns = [
  'Aries', 'Taurus', 'Gemini', 'Cancer', 'Leo', 'Virgo',
  'Libra', 'Scorpio', 'Sagittarius', 'Capricorn', 'Aquarius', 'Pisces'
];

const readingTypes = ['daily', 'weekly', 'monthly', 'general'];

function ReadingForm({ onReadingGenerated, setLoading }) {
  const [formData, setFormData] = useState({
    userName: '',
    birthDate: '',
    birthTime: '12:00',
    birthPlace: '',
    zodiacSign: 'Aries',
    readingType: 'daily'
  });

  const [loading, setFormLoading] = useState(false);
  const [error, setError] = useState('');

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    setError('');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setFormLoading(true);
    setLoading(true);
    setError('');

    try {
      const response = await axios.post(
        'http://localhost:8080/api/astrology/reading',
        formData,
        {
          headers: {
            'Content-Type': 'application/json'
          }
        }
      );

      onReadingGenerated(response.data);
      setFormData({
        userName: '',
        birthDate: '',
        birthTime: '12:00',
        birthPlace: '',
        zodiacSign: 'Aries',
        readingType: 'daily'
      });
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to generate reading. Please make sure the backend is running.');
      console.error('Error:', err);
    } finally {
      setFormLoading(false);
      setLoading(false);
    }
  };

  return (
    <div className="reading-form-container">
      <form onSubmit={handleSubmit} className="reading-form">
        <h2>ðŸ“‹ Get Your Astrological Reading</h2>

        <div className="form-group">
          <label htmlFor="userName">Your Name *</label>
          <input
            type="text"
            id="userName"
            name="userName"
            value={formData.userName}
            onChange={handleInputChange}
            placeholder="Enter your name"
            required
          />
        </div>

        <div className="form-row">
          <div className="form-group">
            <label htmlFor="birthDate">Birth Date *</label>
            <input
              type="date"
              id="birthDate"
              name="birthDate"
              value={formData.birthDate}
              onChange={handleInputChange}
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="birthTime">Birth Time</label>
            <input
              type="time"
              id="birthTime"
              name="birthTime"
              value={formData.birthTime}
              onChange={handleInputChange}
            />
          </div>
        </div>

        <div className="form-group">
          <label htmlFor="birthPlace">Birth Place *</label>
          <input
            type="text"
            id="birthPlace"
            name="birthPlace"
            value={formData.birthPlace}
            onChange={handleInputChange}
            placeholder="City, Country"
            required
          />
        </div>

        <div className="form-row">
          <div className="form-group">
            <label htmlFor="zodiacSign">Zodiac Sign *</label>
            <select
              id="zodiacSign"
              name="zodiacSign"
              value={formData.zodiacSign}
              onChange={handleInputChange}
            >
              {zodiacSigns.map(sign => (
                <option key={sign} value={sign}>{sign}</option>
              ))}
            </select>
          </div>

          <div className="form-group">
            <label htmlFor="readingType">Reading Type *</label>
            <select
              id="readingType"
              name="readingType"
              value={formData.readingType}
              onChange={handleInputChange}
            >
              {readingTypes.map(type => (
                <option key={type} value={type}>
                  {type.charAt(0).toUpperCase() + type.slice(1)}
                </option>
              ))}
            </select>
          </div>
        </div>

        {error && <div className="error-message">{error}</div>}

        <button
          type="submit"
          disabled={loading}
          className="submit-btn"
        >
          {loading ? (
            <>
              <span className="spinner"></span>
              Consulting the stars...
            </>
          ) : (
            'âœ¨ Generate Reading'
          )}
        </button>

        <p className="form-note">
          * Required fields. Your reading will be generated using AI astrology based on your information.
        </p>
      </form>
    </div>
  );
}

export default ReadingForm;
