import React from 'react';
import './ReadingHistory.css';

function ReadingHistory({ readings }) {
  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  if (readings.length === 0) {
    return (
      <div className="reading-history-container">
        <div className="empty-state">
          <p className="empty-icon">üìö</p>
          <h3>No readings yet</h3>
          <p>Generate your first astrological reading to see it here!</p>
        </div>
      </div>
    );
  }

  return (
    <div className="reading-history-container">
      <h2>üìö Reading History</h2>
      <div className="history-list">
        {readings.map((reading, index) => (
          <div key={reading.id || index} className="history-item">
            <div className="item-header">
              <div className="item-title">
                <h4>{reading.userName}</h4>
                <span className="zodiac-badge">‚ôà {reading.zodiacSign}</span>
              </div>
              <span className="item-date">{formatDate(reading.createdAt)}</span>
            </div>
            <div className="item-info">
              <span className="info-badge">üìÖ {reading.birthDate}</span>
              <span className="info-badge">üìç {reading.birthPlace}</span>
            </div>
            <div className="item-preview">
              <p>{reading.aiReadingResponse.substring(0, 150)}...</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default ReadingHistory;
