import React, { useState } from 'react';
import './App.css';
import Header from './components/Header';
import ReadingForm from './components/ReadingForm';
import ReadingDisplay from './components/ReadingDisplay';
import ReadingHistory from './components/ReadingHistory';

function App() {
  const [currentReading, setCurrentReading] = useState(null);
  const [readings, setReadings] = useState([]);
  const [activeTab, setActiveTab] = useState('form');
  const [loading, setLoading] = useState(false);

  const handleReadingGenerated = (reading) => {
    setCurrentReading(reading);
    setReadings([reading, ...readings]);
    setActiveTab('current');
  };

  const handleTabChange = (tab) => {
    setActiveTab(tab);
  };

  return (
    <div className="App">
      <Header />
      <div className="container">
        <div className="tabs">
          <button
            className={`tab-btn ${activeTab === 'form' ? 'active' : ''}`}
            onClick={() => handleTabChange('form')}
          >
            âœ¨ New Reading
          </button>
          <button
            className={`tab-btn ${activeTab === 'current' ? 'active' : ''}`}
            onClick={() => handleTabChange('current')}
            disabled={!currentReading}
          >
            ðŸ“– Current Reading
          </button>
          <button
            className={`tab-btn ${activeTab === 'history' ? 'active' : ''}`}
            onClick={() => handleTabChange('history')}
          >
            ðŸ“š History ({readings.length})
          </button>
        </div>

        <div className="content">
          {activeTab === 'form' && (
            <ReadingForm onReadingGenerated={handleReadingGenerated} setLoading={setLoading} />
          )}
          {activeTab === 'current' && currentReading && (
            <ReadingDisplay reading={currentReading} />
          )}
          {activeTab === 'history' && (
            <ReadingHistory readings={readings} />
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
